mapper.select:
select

from s_menu


		id = #{id,jdbcType=BIGINT}


		AND parent_id = #{parentId,jdbcType=BIGINT}


		AND menu_code CONCAT('%', #{menuCode,jdbcType=VARCHAR}, '%')


		AND menu_name CONCAT('%', #{menuName,jdbcType=VARCHAR}, '%')


		AND menu_desc CONCAT('%', #{menuDesc,jdbcType=VARCHAR}, '%')


		AND func_link CONCAT('%', #{funcLink,jdbcType=VARCHAR}, '%')


		AND open_type = #{openType,jdbcType=TINYINT}


		AND icon CONCAT('%', #{icon,jdbcType=VARCHAR}, '%')


		AND order = #{order,jdbcType=TINYINT}


		AND remark CONCAT('%', #{remark,jdbcType=VARCHAR}, '%')


		AND is_show = #{isShow,jdbcType=TINYINT}


ORDER BY id DESC

SELECT
FROM s_menu WHERE id IN ( SELECT DISTINCT menu_id FROM s_role_menu WHERE role_id IN
#{item,jdbcType=VARCHAR}
)
